class App extends Component {
  constructor(){
    super();

    this.state = {
      monsters : [
        {
          id:'asc1',
          name : 'Frankenstein'
        },
        {
          id:'asr2',
          name : 'Dracula'
        },
        {
          id:'as1w',
          name : 'Zombie'
        },
      ]
    }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users ').then( response => console.log(response) )
  }

  render(){
    return(
      <div className="App">
       {
         this.state.monsters.map( monster => 
          <h1 key={monster.id}>{ monster.name }</h1>
        )
       }
     </div>
    )
  }

}

export default App;
